import { toast } from 'react-toastify';

import AxiosService from '../axiosConfig';

export const uploadImage = async event => {
  let toastId = null;
  try {
    const image = await getImage(event);
    if (!image) return null;

    const formData = new FormData();
    formData.append('image', image); // Remove __filename.name

    console.log('formData:', formData); // Log formData to check if it's populated correctly

    const response = await AxiosService.post('api/upload/', formData, {
      onUploadProgress: ({ loaded, total }) => {
        const progress = Math.round((loaded * 100) / total);
        if (toastId) toast.update(toastId, { progress });
        else toastId = toast.success('Uploading...', { progress });
      },
    });

    console.log('response:', response); // Log response to check if it's populated correctly

    toast.dismiss(toastId);
    
    // Check if response and response.data are defined before accessing imageUrl
    if (response && response.data && response.data.imageUrl) {
      return response.data.imageUrl;
    } else {
      console.error('Error uploading image: Response or response data is undefined or does not contain the imageUrl property.');
      toast.error('Error uploading image', 'Upload Error');
      return null;
    }
  } catch (error) {
    console.error('Error uploading image:', error);
    toast.dismiss(toastId);
    toast.error('Error uploading image', 'Upload Error');
    return null;
  }
};
