import React from 'react';
Date.defaultProps={
    options:{
        weekday:'short',
        year:'numeric',
        month:'long',
        day:'numeric',
        hour:'numeric',
        minute:'numeric',
        second:'numeric',
    },
}
export default function Date({date,
    options:{weekday,year,month,day,hour,minute,second}}){
        let currentLocale = new Intl.DateTimeFormat().resolvedOptions().locale;
        
        const getDate=()=>{
       new Intl.DateTimeFormat(currentLocale,{
        weekday,year,month,day,hour,minute,second
       }).fomat(Date.parse(date))
        }
  return(
    <>{getDate()}</>
  )
}
